// import logo from './logo.svg';
// import './App.css';
import React from 'react';
import { nanoid } from 'nanoid';
import PubNub from 'pubnub';

const pubNubConfig = require('./pubnub.config.json');

class App extends React.Component {
  constructor() {
    super();

    this.state = {
      groupID: '',
      channel: 'yummy',
      inChat: false,
    }
    this.createGroup = this.createGroup.bind(this);
    this.leaveGroup = this.leaveGroup.bind(this);
  }

  createGroup() {
    this.pubNubClient = new PubNub(pubNubConfig.keySet);

    let groupID = nanoid(6);
    let channel = 'yummy--' + groupID;

    this.pubNubClient.subscribe({
      channels: [channel],
      withPresence: true,
    })

    this.setState({
      groupID,
      channel,
      inChat: true,
    });

    alert("Share this group code with your friends: " + groupID);
  }

  leaveGroup() {
    this.setState({
      inChat: false,
    })
  }

  render() {
    return (
      <div>
        <header>
          <p>
            Yummy
          </p>
        </header>
        {
          !this.state.inChat &&
          <div>
            <button onClick={this.createGroup}>
              Create a Group
            </button>
            <button>
              Join a Group
            </button>
          </div>
        }
        {
          this.state.inChat &&
          <div>
            <button onClick={this.leaveGroup}>
              Leave Group
            </button>
          </div>
        }
      </div>
    )
  }
}

export default App;
